/*
    Project : AddMustard
    Author(s) : David Charles
*/

//Dependencies
import { TiltValueInterface, GenerateTiltValuesParametersInterface } from "./helperInterfaces";

//Functions
export const generateRandomNumber : (min? : number, max? : number) => number = (min = 0, max = 100) => {
    return (
        Math.floor( ( Math.random() * (max - min + 1) ) + min )
    )
}

export const generateTiltValues : (GenerateTiltValuesParametersInterface) => TiltValueInterface = (GenerateTiltValuesParametersInterface) => {
    /*
        Creates a random value between -maxTiltVale and +maxTiltValue
        Take random Number between -maxTiltVale and +maxTiltVale
        Multiply the Number by -1 to the power of 1 or 2 (generated randomly)
    */
    
    const rotation : number = generateRandomNumber((-1) * maxRotation, maxRotation) * Math.pow(-1, generateRandomNumber(1, 2));
    const skew : number = generateRandomNumber((-1) * maxSkew, maxSkew) * Math.pow(-1, generateRandomNumber(1, 2));

    const tiltValues : TiltValueInterface = {
        rotation : rotation,
        skew : skew
    };

    return tiltValues;
};
