    //                     cardImage : item.data.cardImage,
    //                     cardLinkType : item.data.cardLinkType,
    //                     cardLinkHref : item.data.cardLinkHref,
    //                     cardBackgroundColor : item.data.cardBackgroundColor,
    //                     cardContent : item.data.cardContent
    //                 }}>
    //                     {
    //                         item.data.cardContent ? 
    //                          <p dangerouslySetInnerHTML={{ __html: item.data.cardContent }} />
