/*
    Project : AddMustard
    Author(s) : David Charles
*/

//Dependencies
import React, { Children } from "react";

//Components
import Button from "../Buttons/Button";
import ImageContainer from "../ImageContainer/ImageContainer";

//Styles
import "./Footer.scss";

//Interfaces
import { FooterInterface, FooterNavigationStyleInterface } from "./FooterInterfaces";

//Images
const googlePartnerLogo : string = require("../../Images/googlePartner_logo.svg").default;
const bingPartnerLogo : string = require("../../Images/bingPartner_logo.svg").default;

//Component
const Footer : React.FC<FooterInterface> = (props : FooterInterface) => {
    //Properties
    const childrenCount : number = Children.toArray(props.children).length;

    //Functions
    const handleScrollToTop : () => void = () => {
        const scrollLeftValue : number = 0;
        const scrollTopValue : number = 0;
        const scrollBehavior : ScrollBehavior = "smooth";

        window.scroll({
            top: scrollTopValue,
            left: scrollLeftValue,
            behavior: scrollBehavior
        });
    };

    //Variables
    const navigationStyle : FooterNavigationStyleInterface = {
        gridTemplateColumns: "repeat(" + childrenCount + ", 1fr)"

    };

    return (
        <footer className="Footer">
            <div className="inner">
                <div className="upper">
                    <div className="inner">
                        <nav>
                            <div className="inner" style={navigationStyle}>
                                {props.children}
                            </div>
                        </nav>

                        <div className="toTopContainer" onClick={handleScrollToTop}>
                            <div className="inner">
                                <div className="toTop" />
                            </div>
                        </div>
                    </div>
                </div>

                <div className="lower">
                    <div className="inner">
                        <div className="left">
                            <div className="inner">
                                <ImageContainer data={{ imageSource: googlePartnerLogo, imageAlt: "Google Partner Logo", imageHrefType : "externalLink", imageHref: "" }} />

                                <ImageContainer data={{ imageSource: bingPartnerLogo, imageAlt: "Bing Partner Logo", imageHrefType : "externalLink", imageHref: "" }} />
                            </div>
                        </div>

                        <div className="right">
                            <div className="inner">
                                <nav>
                                    <div className="inner">
                                        <Button data={{
                                            customClass: "baseNav",
                                            itemCopy: "How can we help?",
                                            itemIcon: "",
                                            itemType: "internalLink",
                                            itemHref: "/how-can-we-help/",
                                            itemFunction: () => { }
                                        }} />

                                        <Button data={{
                                            customClass: "baseNav",
                                            itemCopy: "LinkedIn",
                                            itemIcon: "",
                                            itemType: "externalLink",
                                            itemHref: "#",
                                            itemFunction: () => { }
                                        }} />

                                        <Button data={{
                                            customClass: "baseNav",
                                            itemCopy: "GlassDoor",
                                            itemIcon: "",
                                            itemType: "externalLink",
                                            itemHref: "#",
                                            itemFunction: () => { }
                                        }} />
                                    </div>
                                </nav>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </footer>
    );
};

export default Footer;
