/*
    Project : AddMustard
    Author(s) : David Charles
*/

//Dependencies
import React from "react";
import { Link, LinkProps } from "react-router-dom";
import { generateTiltValues } from "../../Utilities/helpers";

//Styles
import "./ImageContainer.scss";

//Interfaces
import { ImageContainerInterface, TiltImageStyleInterface } from "./ImageContainerInterfaces";
import { GenerateTiltValuesParametersInterface, TiltValueInterface } from "../../Utilities/helperInterfaces";

//Component
const ImageContainer : React.FC<ImageContainerInterface> = (props : ImageContainerInterface) => {
    //Properties
    const imageSource : string = props.data.imageSource;
    const imageAlt : string | undefined = props.data.imageAlt;
    const imageHrefType : string | undefined = props.data.imageHrefType;
    const imageHref : string | LinkProps["to"] = props.data.imageHref || "";

    //Functions
    const generateImage : () => JSX.Element = () => {
        if (imageHref) {
            if (imageHrefType === "internalLink") {
                return (
                    <Link to={imageHref} style={antiTiltStyle}>
                        <img src={imageSource} alt={imageAlt} />
                    </Link>
                )

            } else if (imageHrefType === "externalLink") {
                return (
                    <a href={imageHref} style={antiTiltStyle}>
                        <img src={imageSource} alt={imageAlt} />
                    </a>
                )
            }
        }

        return (
            <img src={imageSource} alt={imageAlt} style={antiTiltStyle} />
        )
    }

    //Variables
    const tiltValueProps : GenerateTiltValuesParametersInterface = {
        maxRotation : 4,
        maxSkew : 4
    };
    const tiltValues : TiltValueInterface = generateTiltValues(tiltValueProps);
    const tiltStyle : TiltImageStyleInterface = {
        transform : "rotate(" + tiltValues.rotation + "deg) skewX(" + tiltValues.skew + "deg)"
    };
    const antiTiltStyle : TiltImageStyleInterface = {
        transform : "rotate(" + ( (-1) * tiltValues.rotation ) + "deg) skewX(" + ( (-1) * tiltValues.skew ) + "deg)"
    }

    return (
        <div className="ImageContainer">
            <div className="inner" style={tiltStyle}>
                {generateImage()}
            </div>
        </div>
    );
};
export default ImageContainer;
