//Dependencies
import React from "react";
import SlideCarousel from "../../../Components/Carousels/SlideCarousel";
import SlideCarouselBlogCard from "../../../Components/Cards/SlideCarouselBlogCard";

//Components
import Tilt from "../../../Components/Tilt/Tilt";

//Interfaces
import { OurWorkSectionInterface } from "../HomePageInterfaces";
import { SlideCarouselInterface } from "../../../Components/Carousels/CarouselInterfaces";
import { SlideCarouselBlogCardInterface } from "../../../Components/Cards/CardInterfaces";

//Component
const OurWorkSection : React.FC<OurWorkSectionInterface> = (props : OurWorkSectionInterface) => {
    //Properties
    const title : string | undefined = props.data.title;
    const carouselData : SlideCarouselInterface | undefined = props.data.carouselData;
    const carouselItems : Array<SlideCarouselBlogCardInterface> | undefined = props.data.carouselItems;

    //Functions
    const generateCarouselItems : () => JSX.Element | Array<JSX.Element> = () => {
        if (carouselItems && carouselItems.length > 0) {
            carouselItems.map((item : SlideCarouselBlogCardInterface, key : number) => {
                console.log(item)
                return (
                    <SlideCarouselBlogCard key ={key} data={{
                        cardLogoImage : item.data.cardLogoImage,
                        cardImage : item.data.cardImage,
                        cardLinkType : item.data.cardLinkType,
                        cardLinkHref : item.data.cardLinkHref,
                        cardBackgroundColor : item.data.cardBackgroundColor,
                        cardContent : item.data.cardContent
                    }}>
                        {
                            item.data.cardContent ? 
                             <p dangerouslySetInnerHTML={{ __html: item.data.cardContent }} />
                             :
                             <></>
                        }
                    </SlideCarouselBlogCard>
                )
            })
        }

        return <></>
    };

    return (
        <section className="OurWorkSection">
            <div className="inner"> 
            {
                title ?
                    <div className="titleContainer">
                        <div className="inner">
                            <Tilt data={{ maxRotation : 2, maxSkew : 2, backgroundColor : "#000000" }} />

                            <h1>{title}</h1>
                        </div>
                    </div>
                    :
                    <></>
            }

                <SlideCarousel data={{
                    carouselHasArrows : carouselData.data.carouselHasArrows,
                    carouselHasDots : carouselData.data.carouselHasDots,
                    carouselViewAllCopy : carouselData.data.carouselViewAllCopy,
                    carouselViewAllType : carouselData.data.carouselViewAllType,
                    carouselViewAllHref : carouselData.data.carouselViewAllHref
                }}>
                    {generateCarouselItems()}
                    {/* <SlideCarouselBlogCard data={{
                        cardLogoImage : "/images/ASP_logo.png",
                        cardImage : "/images/ASP_blogImage.png",
                        cardLinkType : "internalLink",
                        cardLinkHref : "/",
                        cardBackgroundColor : "#000",
                    }}>
                        <p>Adopting a brand-centric approach to marketing, <span className="tilt" style={{ backgroundColor : "#F0BEC9" }}>driving growth in sales</span> & margins in luxury retail</p>
                    </SlideCarouselBlogCard>

                    <SlideCarouselBlogCard data={{
                        cardLogoImage : "/images/MAP_logo.png",
                        cardImage : "/images/MAP_blogImage.png",
                        cardLinkType : "internalLink",
                        cardLinkHref : "/",
                        cardBackgroundColor : "#000"
                    }}>
                        <p>The impact of <span className="tilt" style={{ backgroundColor : "#F27250" }}>improving online experience</span> on marketing efficiency</p>
                    </SlideCarouselBlogCard>

                    <SlideCarouselBlogCard data={{
                        cardLogoImage : "/images/FCG_logo.png",
                        cardImage : "/images/FCG_blogImage.png",
                        cardLinkType : "internalLink",
                        cardLinkHref : "/",
                        cardBackgroundColor : "#000"
                    }}>
                        <p><span className="tilt" style={{ backgroundColor : "#F0BEC9" }}>Brand evolution</span> engagement</p>
                    </SlideCarouselBlogCard> */}
                </SlideCarousel>
            </div>
        </section>
    );
};
export default OurWorkSection;
