/*
    Project : AddMustard
    Author(s) : David Charles
*/

//Dependancies
import React from "react";
import {Children, useRef, useState} from "react";

//Components
import Button from "../Buttons/Button";

//Styles
import "./Grid.scss";

//Interfaces
import { GridInterface } from "./GridInterfaces";

//Component
const Grid : React.FC<GridInterface> = (props : GridInterface) => {
    //Properties
    const childrenCount : number = Children.count(props.children);
    const childrenArray : Array<React.ReactNode> = Children.toArray(props.children);
    const gridClass : string | number = props.data.gridClass;
    const gridLoadCount : number = props.data.gridLoadCount;
    const gridPagination : string | undefiend = props.data.gridgridPagination;
    const gridPaginationCopy : string | undefined = props.data.gridPaginationCopy;
    const gridPaginationLinkHref : string | undefined = props.data.gridPaginationLinkHref;
    const gridPaginationLinkType : string | undefined = props.data.gridPaginationLinkType;
    
    //states
    var [currentActiveItemsCount, setCurrentActiveItemsCount] = useState<number>(gridLoadCount);
    var [loadMoreButtonHidden, setLoadMoreButtonHidden] = useState<boolean>((gridLoadCount > childrenCount));

    //refs
    const pagedRef = useRef(); //any

    //functions
    const generatePaginationContainer = () => {
        if (gridPagination === "none") {
            return <></>

        } else if (gridPagination === "link") {
            return (
                <div className="paginationContainer link">
                    <div className="inner">
                        {/* <Button data={{
                            customClass : "",
                            itemCopy : gridPaginationCopy,
                            itemIcon : "",
                            itemType : gridPaginationLinkType,
                            itemHref : gridPaginationLinkHref,
                            itemFunction : () => {}
                        }} /> */}
                    </div>
                </div>
            )

        } else if (gridPagination === "loadMore") {
            if (loadMoreButtonHidden) {
                return <></>

            } else {
                return (
                    <div className="paginationContainer loadMore">
                        <div className="inner">
                            {/* <Button data={{
                                customClass : "",
                                itemCopy : gridPaginationCopy,
                                itemIcon : "",
                                itemType : "function",
                                itemHref : "",
                                itemFunction : handleLoadMore
                            }} /> */}
                        </div>
                    </div>
                )
            }

        } else if (gridPagination === "paged") {
            return (
                <div className="paginationContainer paged">
                    <div className="inner">
                        {/* <Button ref={pagedRef} data={{
                            customClass : "",
                            itemCopy : gridPaginationCopy,
                            itemIcon : "",
                            itemType : "function",
                            itemHref : "",
                            itemFunction : handlePaged
                        }} /> */}
                    </div>
                </div>
            )

        } else {
            return <></>
        }
    };
    const handleLoadMore = () => {
        var newActiveItemCount : number = currentActiveItemsCount + gridLoadCount > childrenCount ? childrenCount : currentActiveItemsCount + gridLoadCount;

        if (newActiveItemCount === childrenCount) {
            setLoadMoreButtonHidden(true);

        }

        setCurrentActiveItemsCount(newActiveItemCount);
    };
    const handlePaged = () => {
        console.log("handle grid paged function");
    }

    //variables
    const GridClass : string = gridClass ? "Grid " + gridClass : "Grid";
    
    return (
        <div className={GridClass}>
            <div className="outer">
                <div className="inner">
                    {
                        childrenCount > 0 ?
                            childrenArray.map((child, key) => {
                                const childClass = currentActiveItemsCount - 1 >= key ? "item active" : "item"; //string

                                return (
                                    <div className={childClass} key={key}>
                                        {child}
                                    </div>
                                )
                            })
                            :
                            <></>
                    }
                </div>

                {generatePaginationContainer()}
            </div>
        </div>
    );
};

export default Grid;
