/*
	Project : AddMustard
	Author(s) : David Charles
*/

//Dependencies
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";

//Components
import Header from "./Components/Header/Header";
import TiltButton from './Components/Buttons/TiltButton';
import Footer from "./Components/Footer/Footer";
import HomePage from "./Pages/HomePage/HomePage";
import WhatWeDoPage from "./Pages/WhatWeDoPage/WhatWeDoPage";
import WhoWeArePage from './Pages/WhoWeArePage/WhoWeArePage';
import HowCanWeHelpPage from "./Pages/HowCanWeHelpPage/HowCanWeHelpPage";

//Styles
import './App.scss';

//Interfaces
import { AppInterface } from './AppInterfaces';

//Images
const whatsAppIcon : string = require("./Images/whatsApp_icon.svg").default;
const phoneIcon : string = require("./Images/phone_icon.svg").default;
const emailIcon : string = require("./Images/email_icon.svg").default;

//Component
const App : React.FC<AppInterface> = (props : AppInterface) => {
	return (
		<div className="App">
			<Router>
				<Header>
					<TiltButton data={{
						buttonClass: "headerNav",
						buttonCopy: "What we do",
						buttonType: "internalLink",
						buttonHref: "/what-we-do/",
						tiltBackgroundColor : "#000"
					}} />

					<TiltButton data={{
						buttonClass: "headerNav",
						buttonCopy: "Who we are",
						buttonType: "internalLink",
						buttonHref: "/who-we-are/",
						tiltBackgroundColor : "#000"
					}} />

					<TiltButton data={{
						buttonClass: "headerNav",
						buttonCopy: "How can we help?",
						buttonType: "internalLink",
						buttonHref: "/how-can-we-help/",
						tiltBackgroundColor : "#000"
					}} />
				</Header>

				<main>
					<Routes>
						<Route path="/" element={
							<HomePage />
						} />

						<Route path="/what-we-do/" element={
							<WhatWeDoPage />
						} />

						<Route path="/who-we-are/" element={
							<WhoWeArePage />
						} />

						<Route path="/how-can-we-help/" element={
							<HowCanWeHelpPage />
						} />
					</Routes>
				</main>

				<Footer>
					<TiltButton data={{
						buttonClass: "footerNav",
						buttonCopy: "Whatsapp",
						buttonIcon: whatsAppIcon,
						buttonType: "externalLink",
						buttonHref: "https://google.com/",
						tiltBackgroundColor : "#FFF"
					}} />

					<TiltButton data={{
						buttonClass: "footerNav",
						buttonCopy: "Phone",
						buttonIcon: phoneIcon,
						buttonType: "externalLink",
						buttonHref: "#",
						tiltBackgroundColor : "#FFF"
					}} />

					<TiltButton data={{
						buttonClass: "footerNav",
						buttonCopy: "Email",
						buttonIcon: emailIcon,
						buttonType: "externalLink",
						buttonHref: "#",
						tiltBackgroundColor : "#FFF"
					}} />
				</Footer>
			</Router>
		</div>
	);
};
export default App;
