/*
    Project : AddMustard
    Author(s) : David Charles
*/

//Dependencies
import React from "react";
import {Children, useState} from "react";

//Components

//Styles
import "./Carousel.scss";

//Interfaces
import { StackCarouselInterface } from "./CarouselInterfaces";

//Component
const StackCarousel : React.FC<StackCarouselInterface> = (props : StackCarouselInterface) => {
    //Properties
    const childrenCount : number = Children.count(props.children);
    const childrenArray : Array<React.ReactNode> = Children.toArray(props.children);
    const carouselClass : string | undefined = props.data.carouselClass;
    
    //States

    //Functions

    //Variables
    const StackCarouselClass : string = carouselClass ? "StackCarousel " + carouselClass : "StackCarousel";
    
    return (
        <div className={StackCarouselClass}>
            <div className="outer">
                <div className="inner">
                    {
                        childrenCount > 0 ?
                            childrenArray.map((child : React.ReactNode, key : number) => {
                                const childClass : string = "item";

                                return (
                                    <div className={childClass} key={key}>
                                        {child}
                                    </div>
                                )
                            })
                            :
                            <></>
                    }
                </div>
            </div>
        </div>
    );
};
export default StackCarousel;
