/*
    Project : AddMustard
    Author(s) : David Charles
*/

//Dependencies
import React from "react";

//Components
import ImageContainer from "../ImageContainers/TiltImageContainer";
import Tilt from "../Tilt/Tilt";

//Interfaces
import { TestimonialCardInterface } from "./CardInterfaces";

//Images
const quotationMarkLeft : string =  require("../../Images/whatsApp_icon.svg").default;
const quotationMarkRight : string =  require("../../Images/whatsApp_icon.svg").default;

//Component
const TestimonialCard : React.FC<TestimonialCardInterface> = (props : TestimonialCardInterface) => {
    //Properties
    const cardClass : string | undefined = props.data.cardClass;
    const cardTitle : string | undefined = props.data.cardTitle;
    const cardCopy : string | undefined = props.data.cardCopy;
    const cardAuthor : string | undefined = props.data.cardAuthor;
    const cardAuthorReference : string | undefined = props.data.cardAuthorReference;
    const cardBackgroundColor : string | undefiend = props.data.cardBackgroundColor || "";

    return (
        <section className="TestimonialCard">
            <div className="inner"> 
                <div className="contentContainer">
                    <div className="inner">
                        { cardTitle ? <h3>{cardTitle}</h3> : <></>}

                        {cardCopy ? <p>{cardCopy}</p> : <></>}
                    </div>
                </div>

                <div className="cardAuthorContainer">
                    <Tilt data={{ maxRotation : 4, maxSkew : 4, backgroundColor: cardBackgroundColor }} />
                    <p>
                        <span>{cardAuthor}</span>
                        -
                        <span>{cardAuthorReference}</span>
                    </p>
                </div>
            </div>
        </section>
    );
};
export default TestimonialCard;
