/*
    Project : AddMustard
    Author(s) : David Charles
*/

//Dependencies
import React, { useEffect, useState } from "react";
import axios from "axios";

//Components
import BlogSection from "./Sections/BlogSection";
import TestimonialSection from "./Sections/TestimonialSection";
import AboutSection from "./Sections/AboutSection";
import OurWorkSection from "./Sections/OurWorkSection";
import IntroSection from "./Sections/IntroSection";

//Styles
import "./HomePage.scss";

//Interfaces
import { HomePageInterface } from "./HomePageInterfaces";
import ImageContainer from "../../Components/ImageContainers/ImageContainer";
import TiltButton from "../../Components/Buttons/TiltButton";

//Component
const HomePage : React.FC<HomePageInterface> = (props : HomePageInterface) => {
    var [introSectionData, setIntroSectionData] = useState<any>([]);
    console.log(introSectionData["introSectionData"]);

    //Effects
    useEffect(() => {
        axios.get("./data/HomePageData.json")
            .then((result : any) => { setIntroSectionData(result.data) })
            .then((error : any) => { console.log(error); });
    }, []);

    return (
        <div id="HomePage">
            <div className="inner">
                <IntroSection data={{}}>
                    <div className="topSection">
                        <div className="inner">
                            <div className="backgroundImage">
                                <div className="inner">
                                    <ImageContainer data={{ imageSource : "./images/introSection_1.png" }} />
                                </div>
                            </div>

                            <div className="contentContainer">
                                <div className="inner">
                                    <h2><TiltButton data={{ buttonCopy : "Add", tiltBackgroundColor : "#000000", tiltMaxRotation : 4, tiltMaxSkew : 4 }} /> the strategic and creative fire-power of an advertising agency, to the precision and incision of a digital marketing agency,  to the flexibility and responsiveness of an in-house team.</h2>

                                    <TiltButton data={{ buttonCopy : "And then you get us.", tiltBackgroundColor : "#000000", tiltMaxRotation : 2, tiltMaxSkew : 2 }} />
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="bottomSection">
                        <div className="inner">
                            <div className="backgroundImage">
                                <div className="inner">
                                    <ImageContainer data={{ imageSource : "./images/introSection_2.png" }} />
                                </div>
                            </div>

                            <div className="contentContainer">
                                <div className="inner">
                                    <p>
                                        Take technology.<br />
                                        Add marketing nous, <br />
                                        creativity and a healthy <br />
                                        dose of entrepreneurial spirit.<br /> 
                                        Take away smoke mirrors<br />
                                        and arrogance.
                                    </p>

                                    <TiltButton data={{ buttonCopy : "How can we help", buttonType : "internalLink", buttonHref : "#", tiltBackgroundColor : "#000000", tiltMaxRotation : 2, tiltMaxSkew : 2 }} />
                                </div>
                            </div>
                        </div>
                    </div>
                </IntroSection>

                <OurWorkSection data={{}} />

                <AboutSection data={{}} />

                <TestimonialSection data={{}} />

                <BlogSection data={{}} />
            </div>
        </div>
    );
};
export default HomePage;
